MQTT_TRANSPORTS = []


def gather_mqtt(apps, schema_editor):
    baseModel = apps.get_model('device', 'transport')
    protocol = apps.get_model('orchestrator', 'protocol').objects.get(name='MQTT')

    for t in baseModel.objects.filter(protocol=protocol):
        d = dict(
            serialization=[s.pk for s in t.serialization.all()],
            **t.__dict__
        )
        d.pop('_state', None)
        d.pop('polymorphic_ctype_id', None)
        MQTT_TRANSPORTS.append(d)


def update_mqtt(apps, schema_editor):
    baseModel = apps.get_model('device', 'transport')
    mqttModel = apps.get_model('device', 'transportmqtt')
    ContentType = apps.get_model('contenttypes', 'ContentType')
    mqtt_ct = ContentType.objects.get_for_model(mqttModel)

    for data in MQTT_TRANSPORTS:
        base_trans = baseModel.objects.get(transport_id=data['transport_id'])
        devs = list(base_trans.device_set.all())
        base_trans.delete()
        serials = data.pop('serialization', [])
        trans = mqttModel.objects.create(
            **data,
            polymorphic_ctype=mqtt_ct
        )
        trans.serialization.set(serials)
        trans.save()
        for d in devs:
            d.transport.add(trans)
            d.save()


